classdef Audio_Signals_Analyzer < matlab.apps.AppBase

    % Properties that correspond to app components
    properties (Access = public)
        UIFigure                  matlab.ui.Figure
        AudioFileSelectionPanel   matlab.ui.container.Panel
        GridLayout2               matlab.ui.container.GridLayout
        MergeselectedfilesButton  matlab.ui.control.Button
        DeleteButton              matlab.ui.control.Button
        AddButton                 matlab.ui.control.Button
        ListBox                   matlab.ui.control.ListBox
        SoundSettingPanel         matlab.ui.container.Panel
        GridLayout                matlab.ui.container.GridLayout
        Slider                    matlab.ui.control.Slider
        StopButton_2              matlab.ui.control.Button
        PauseResumeButton         matlab.ui.control.StateButton
        PlayButton                matlab.ui.control.Button
        al                        matlab.ui.control.Label
        tfile                     matlab.ui.control.Label
        UIAxes3                   matlab.ui.control.UIAxes
        UIAxes2                   matlab.ui.control.UIAxes
        UIAxes                    matlab.ui.control.UIAxes
        ContextMenu               matlab.ui.container.ContextMenu
        Menu                      matlab.ui.container.Menu
        Menu2                     matlab.ui.container.Menu
    end



    % Callbacks that handle component events
    methods (Access = private)

        % Button pushed function: DeleteButton
        function DeleteButtonPushed(app, event)
            [~,index] = ismember(app.ListBox.Value, app.ListBox.ItemsData);
            app.ListBox.Items(index) = [];
            app.ListBox.ItemsData(index) = [];
            cla(app.UIAxes);
            cla(app.UIAxes2);
            cla(app.UIAxes3);
            if (isempty(app.ListBox.Items) ~= true)
                ListBoxValueChanged(app, event);
            end 
        end

        % Button pushed function: AddButton
        function AddButtonPushed(app, event)
            clear y;
            [filename,filepath] = uigetfile('*.wav');  %open a mat file
            filepath = strcat(filepath,filename);
            if (isempty(filepath) ~= true)
                app.tfile.Text = filepath;
                [y, fs] = audioread(filepath);
                t = (0:length(y)) / fs;
                t(end) = [];
                app.al.Text = strcat(num2str(t(end))," Second");
                plot(app.UIAxes,t, y);
                h=zoom(app.UIAxes);
                h.Motion = 'horizontal';
                h.Enable = 'on';
            
                itemList = app.ListBox.Items;
                itemData = app.ListBox.ItemsData;
                itemList{end+1} = filename;
                itemData{end+1} = filepath;
                app.ListBox.Items = itemList;
                app.ListBox.ItemsData = itemData;
            end
            
            
        end

        % Value changed function: ListBox
        function ListBoxValueChanged(app, event)
            [~,index] = ismember(app.ListBox.Value, app.ListBox.ItemsData);
            value = app.ListBox.ItemsData{index};
            app.tfile.Text = value;
            [y, fs] = audioread(value);
            t = (0:length(y)) / fs;
            t(end) = [];
            plot(app.UIAxes,t, y);
            
            k = app.Slider.Value;
            global player
            player = audioplayer(y*k, fs);
            
        end

        % Button pushed function: PlayButton
        function PlayButtonPushed(app, event)
            global player
            play(player);
        end

        % Button pushed function: StopButton_2
        function StopButton_2Pushed(app, event)
            global player
            stop(player);
        end

        % Value changed function: PauseResumeButton
        function PauseResumeButtonValueChanged(app, event)
            value = app.PauseResumeButton.Value;
            global player
            if (value == 1) 
                pause(player);
            else
                resume(player);
            end
        end

        % Value changed function: Slider
        function SliderValueChanged(app, event)
            global player
            [~,index] = ismember(app.ListBox.Value, app.ListBox.ItemsData);
            app.tfile.Text = string(isplaying(player));
            if (index ~= 0.0 & isplaying(player) ~= false)
                value = app.ListBox.ItemsData{index};
                pause(player);
                NewStart = get(player, 'CurrentSample')+1;
                stop(player);
                k = app.Slider.Value;
                [y, fs] = audioread(value);
                player = audioplayer(y*k, fs);
                play(player, NewStart);
            end
        end

        % Callback function
        function TestOiasdladljashbdfkashfsaButtonPushed(app, event)
            [filename,filepath] = uigetfile('*.wav');  %open a mat file
            filepath = strcat(filepath,filename);
            [y1, fs1] = audioread(filepath);
            
            [filename,filepath] = uigetfile('*.wav');  %open a mat file
            filepath = strcat(filepath,filename);
            [y2, fs2] = audioread(filepath);
            
            lenY = size(y1, 1);
            lenZ = size(y2, 1);
            len = max(lenY, lenZ);
            S = zeros(len, size(y1, 2));
            S(1:lenY, :) = y1;
            S(1:lenZ, :) = S(1:lenZ, :) + y2;
            maxValue = max(abs(S(:)));
            S = S / maxValue;
            if (length(y1) >= length(y2))
                t = (1:length(y1)) / fs1;
                f = fs1;
            else
                t = (1:length(y2)) / fs2;
                f = fs2;
            end
            plot(app.UIAxes,t, S);
            soundsc(S,f);
            
            
            
            
            %minimumLength = min([length(y1), length(y2)]);
            %y1 = y1(1:minimumLength);
            %y2 = y2(1:minimumLength);
            %y3 = y1+y2;
            %t = (1:minimumLength) / 44100;
            %plot(app.UIAxes,t, y3*0.5);

        end

        % Button pushed function: MergeselectedfilesButton
        function MergeselectedfilesButtonPushed(app, event)
            filePath = app.ListBox.Value;
            global player
            if (length(filePath) == 1)
                [y, fs] = audioread(filePath{1});
                t = (0:length(y)) / fs;
                t(end) = [];
                plot(app.UIAxes2,t, y); 
                %plot(app.UIAxes3,t,y);
                x = y;
                df = fs/length(x);
                f = -fs/2:df:fs/2-df;
                fftA = fftshift(fft(x))/length(fft(x));
                
                plot(app.UIAxes3, f, abs(fftA));
                %title ('FFT Spectrum');
                k = app.Slider.Value;
                player = audioplayer(y*k, fs);
               return
            end
            
            for i = 2:length(filePath)
                if (i == 2)
                    [y1, fs1] = audioread(filePath{1});
                    
                    [y2, fs2] = audioread(filePath{2});
                    
                    lenY = size(y1, 1);
                    lenZ = size(y2, 1);
                    len = max(lenY, lenZ);
                    S = zeros(len, size(y1, 2));
                    S(1:lenY, :) = y1;
                    S(1:lenZ, :) = S(1:lenZ, :) + y2;
                    maxValue = max(abs(S(:)));
                    S = S / maxValue;
                    if (length(y1) >= length(y2))
                        t = (1:length(y1)) / fs1;
                        fs = fs1;
                    else
                        t = (1:length(y2)) / fs2;
                        fs = fs2;
                    end
                else
                    [y, fs_] = audioread(filePath{i});
                    y_dum = S;
                    fs_dum = fs;
                    
                    lenY = size(y_dum, 1);
                    lenZ = size(y, 1);
                    len = max(lenY, lenZ);
                    S = zeros(len, size(y_dum, 2));
                    S(1:lenY, :) = y_dum;
                    S(1:lenZ, :) = S(1:lenZ, :) + y;
                    maxValue = max(abs(S(:)));
                    S = S / maxValue;
                    if (length(y_dum) >= length(y))
                        t = (1:length(y_dum)) / fs;
                        fs = fs_dum;
                    else
                        t = (1:length(y)) / fs_;
                        fs = fs_;
                    end
                end
            end
            plot(app.UIAxes2,t, S);
            x = S;
            Fs = fs;
            df = Fs/length(S);
            f = -Fs/2:df:Fs/2-df;
            fftA = fftshift(fft(x))/length(fft(x));
            
            plot(app.UIAxes3, f, abs(fftA));
            k = app.Slider.Value;
            player = audioplayer(S*k, Fs);
        end
    end

